import * as React from 'react';
import {View, ImageBackground, Text, StyleSheet, TextInput, TouchableWithoutFeedback, Keyboard, TouchableOpacity} from 'react-native';
import Colors from "../../utils/constants/Colors";
import Button from "../../components/Button";
import i18n from "../../utils/locales/LocalesConfig";
import Title from "../../components/Title";
import DismissView from "../../components/hoc/DismissView";
import {connect} from "react-redux";
import AlertConfig from "../../utils/alert/AlertConfig";

type IProps = {
    navigation: any,
    alert?: AlertConfig
}

const MainScreen: React.FC<IProps> = props => {
    const nav = props.navigation

    return (
        <DismissView style={styles.wrapper}>
            <ImageBackground
                style={styles.image}
                source={require('fearless-app/assets/img/home_background.png')}>

                <View style={styles.content}>
                    <Title
                        text={i18n.t('main_titleText')}
                        style={styles.title}
                        fontFamily="SF Heavy"/>

                    <Button
                        onPress={() => nav.navigate('registration')}
                        text={i18n.t('main_btnText')}/>

                    <Text
                        style={styles.login}
                        onPress={() => nav.navigate('login')}>
                        {i18n.t('main_loginText')}
                    </Text>
                </View>
            </ImageBackground>
        </DismissView>
    );
}

const styles = StyleSheet.create({
    wrapper: {
        height: '100%',
        width: '100%',
        backgroundColor: Colors.backgroundDark
    },
    image: {
        height: '100%',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
    },
    content: {
        height: '100%',
        width: '80%',
        alignItems: 'center',
        justifyContent: 'flex-end',
        paddingBottom: 50,
    },
    title: {
        textAlign: 'center',
        marginBottom: 15,
    },
    login: {
        marginTop: 15,
        color: Colors.gray,
        fontFamily: 'SF Medium',
        fontSize: 16,
    },
})

export default connect()(MainScreen)
