import {AlertActionType, IAlert, SHOW_ALERT} from "./alertType";

export function showAlert(object: IAlert): AlertActionType {
    if (object.status === undefined) object.status = true
    return {
        type: SHOW_ALERT,
        payload: {
            text: object.text,
            type: object.type,
            status: object.status,
            isShowing: object.status
        }
    }
}
