export const SHOW_ALERT = 'SHOW_ALERT'

/* For state */
export interface IAlert {
    text: string,
    type?: 'message' | 'error',
    status?: boolean
    isShowing?: boolean
}

/* For action */
export interface IShowAlert {
    type: typeof SHOW_ALERT,
    payload: IAlert
}

export type AlertActionType = IShowAlert
