import React from 'react';
import {View, FlatList, StyleSheet, Text, ImageBackground, ScrollView} from 'react-native'
import DismissView from "../../../components/hoc/DismissView";
import Colors from "../../../utils/constants/Colors";
import {isApple} from "../../../utils/constants/Ios";
import ProgressBar from "../../../components/ProgressBar";
import {IRegistrationType} from "../../../utils/interfaces/IRegistration";
import Button from "../../../components/Button";
import i18n from '../../../utils/locales/LocalesConfig'
import {WheelPicker} from "react-native-wheel-picker-android";

const WeightScreen: React.FC<IRegistrationType> = props => {

    const wheelPickerData = [
        "sunday",
        "monday",
        "tuesday",
        "wednesday",
        "thursday",
        "friday"
    ];

    return (
        <View style={styles.wrapper}>

            <ImageBackground
                style={styles.image}
                source={require('fearless-app/assets/img/create_account_bg.png')}>

                <DismissView style={styles.content}>

                    <ProgressBar
                        navigation={props.navigation}
                        navigateTo="main"
                        currentPage={props.page}/>

                    <View style={styles.contentContainer}>
                        <WheelPicker
                            backgroundColor="red"
                            itemTextColor="grey"
                            selectedItem={1}
                            data={wheelPickerData}
                            itemTextFontFamily='SF Display'
                            selectedItemTextFontFamily='SF Light'/>
                    </View>

                    <Button
                        text={i18n.t("btnNext")}
                        // onPress={ () =>props.navigation.navigate()}
                        style={styles.btnNext}/>
                </DismissView>
            </ImageBackground>
        </View>
    );
};

const styles = StyleSheet.create({
    wrapper: {
        height: '100%',
        width: '100%',
        backgroundColor: Colors.backgroundDark
    },
    image: {
        height: '100%',
        width: '100%',
    },
    btnNext: {
        width: '85%',
    },
    content: {
        flex: 1,
        paddingVertical: isApple ? 50 : 20,
        alignItems: 'center',
        justifyContent: 'space-between',
    },
    contentContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    genderContainer: {
        width: '85%',
        height: '17%',
        flexDirection: 'row',
    },
    genderItem: {
        paddingHorizontal: 10,
        width: '50%',
    },
})


export default WeightScreen
