import {IAlert, AlertActionType, SHOW_ALERT} from "./alertType";

const initialState: IAlert = {
    status: false,
    text: 'Null',
    type: 'error',
    isShowing: false
}

export default function alert(state = initialState, action: AlertActionType) {
    switch (action.type) {
        case SHOW_ALERT:
            return {
                status: action.payload.status,
                text: action.payload.text,
                type: action.payload.type || state.type,
                isShowing: action.payload.isShowing
            }
        default:
            return state
    }
}
