import React, {useState} from 'react';
import {StyleSheet, Text, Animated} from 'react-native';
import colors from '../utils/constants/Colors';
import {isApple} from "../utils/constants/Ios";
import {connect} from "react-redux";
import {AppState} from "../redux/_store/reduxStore";
import {IAlert} from "../redux/alert/alertType";
import AlertConfig from "../utils/alert/AlertConfig";

const Alert: React.FC<IAlert> = (props) => {
    const primaryY = -150
    const targetY = 0

    const [messagePosition] = useState(new Animated.Value(primaryY));
    const messageType = props.type
    const background = messageType === 'error' ? colors.error : colors.primary
    const messageStyles = [styles.wrapper, {top: messagePosition}, isApple ? styles.ios_wrapper : null]

    const alert = new AlertConfig()

    function onUpdate() {
        const params = {
            text: props.text,
            status: false,
            isShowing: false,
        }
        alert.show(params, true)
    }

    if (props.status) {
        let p = new Promise(function (resolve, reject) {

            Animated.timing(messagePosition, {
                toValue: targetY,
                duration: 700,
            }).start();
            resolve()
        });
        p.then(() => {

            setTimeout(() => {
                Animated.timing(messagePosition, {
                    toValue: primaryY,
                    duration: 700,
                }).start();
                setTimeout(() => {

                    onUpdate()
                }, 500)
            }, 2500)
        })

    }

    return (
        <Animated.View style={[messageStyles, {backgroundColor: background}]}>
            <Text style={styles.text}>{props.text}</Text>
        </Animated.View>
    );
}

const styles = StyleSheet.create({
    wrapper: {
        width: '100%',
        height: 'auto',
        minHeight: 30,
        position: 'absolute',
        left: 0,
        zIndex: 1000,
        alignItems: 'center',
        justifyContent: 'center',
        paddingLeft: 10,
        paddingRight: 10,
        paddingTop: 5,
        paddingBottom: 5,
    },
    ios_wrapper: {
        alignItems: 'center',
        justifyContent: 'flex-end',
        paddingTop: 35,
    },
    text: {
        color: 'white',
        fontFamily: 'SF Medium',
        textAlign: 'center'
    }
});

function mapStateToProps(state: AppState): IAlert {
    return {
        status: state.alert.status,
        text: state.alert.text,
        isShowing: state.alert.isShowing,
        type: state.alert.type
    }
}

export default connect(mapStateToProps)(Alert);
